    if(seq.length == 0) return true
    print("recoverSeq|" + child.field('Hostname') + "|" + seq[0].field('Sequence'))
    var sql = 'UPDATE ' + child.field('@rid') + ' SET Sequence = "'+seq[0].field('Sequence')+'"'
    retry("db.command('" + sql + "')")
