    function retry(command){
        try {
            eval(command) 
        }
        catch(err){
            var e = '' + err
            if(e.indexOf('UPDATE') > 0) {
                print('Retrying ' + command)
                retry(command)
            }
            else {
              db.command('INSERT INTO Errors Set Command = ?, Message = ?',command, e)
              print('Failed: ' + command + ' | Error: ' + e)
           }
        }
    }
    var pc = null
    var ua = '' + u
