      print('\nForeground transit from: ')
      print(u.getProperty('@rid') + ' ' + u.getProperty('FromProcessId') + ' to ' + u.getProperty('ToProcessId') + '\n'); 

      if(u.getProperty('FromProcessGuid') == null) {
